import { useState, UseEffect} from "react";
import { View,Text } from "react-native";
import { TextInput, Avatar, Button } from "react-native-paper";
import { styles } from "../assets/estilos/alistyle";
//firebase
import {getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword} from 'firebase/auth'
import {initializeApp} from 'firebase/app';
import {firebaseConfig} from '../firebaseconfig';
import {set} from 'react-hook-form';

export default function Loguin({navigation}){
    const [nombreusuario, setNombreUsuario] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [showPass,setShowpass] = useState(false)
    const [messageColor, setMessageColor] = useState(true)

    //Definir constantes para la autenticacion
    const app = initializeApp(firebaseConfig);
    const auth = getAuth(app)
    //Metodos para crear cuenta en firebase authentication y signIn
    const handleCreateAccount = ()=>{
        createUserWithEmailAndPassword(auth,nombreusuario,password)
        .then((userCredential)=>{
            //console.log(userCredential.user.providerData)
            setMessageColor(true)
            setMessage("Cuenta creada correctamente ...")
        })
        .catch((errror)=>{
            //console.log(error.message)
            setMessage("Error al crear la cuenta ... Intentelo de nuevo")
            setMessageColor(false)
        })
    }
    const handleSignIn = ()=>{
        signInWithEmailAndPassword(auth, nombreusuario, password)
        .then((userCredential)=>{
            console.log("Conexion exitosa...");
            navigation.navigate('Home', {nombreusuario:nombreusuario})
        })
        .catch((error)=>{
            //console.log(error.message)
            setMessage("contrase침a incorrecta.");
            setMessageColor(false)
        })
    }

    return(
        <View style={styles.container}>
        <Avatar.Image
            style={{ marginBottom: 20 }}
            size={100}
            source={require('../assets/imgs/imagen.jpg')} />
        <View style={{ borderWidth: 5, borderColor: 'gray', borderRadius: 15, padding: 45 }}>
            <TextInput
                autoFocus
                label="Nombre de Usuario"
                left={<TextInput.Icon icon="account" />}
                onChangeText={(email) => setNombreUsuario(email)}
                value={email}
            />
            <TextInput
                style={{marginTop: 10}}
                label="Nombre"
                left={<TextInput.Icon icon="account" />}
                onChangeText={(email) => setEmmail(email)}
                value={email}
            />
            <TextInput
                style={{ marginTop: 10 }}
                label="Contrase침a"
                secureTextEntry ={!showPass}
                onChangeText={(password) => setPassword(password)}
                value={password}
                right={<TextInput.Icon icon={showPass ? "eye" : "eye-off"} onPress={()=> setShowpass(!showPass)} />}
            />
            <Button
                style={{ marginTop: 10, backgroundColor: 'orange' }}
                icon="login"
                mode="outlined"
                onPress={handleSignIn}
            >
                Iniciar Sesi칩n
            </Button>
            <Button
                style={{ marginTop: 20, backgroundColor: 'yellow' }}
                icon="account"
                mode="outlined"
                onPress={handleCreateAccount}
            >
                Crear Cuenta
            </Button>
            <Text style={{ marginTop: 5, color: messageColor?'green': 'red'}}>{message}</Text>
        </View>
    </View>
  );
}
