import React from "react";
import { View, Text, ScrollView, StyleSheet, FlatList, Platform} from "react-native";
import { useForm, Controller } from 'react-hook-form';
import { TextInput, Button } from "react-native-paper";
import { useState } from "react";
import { styles } from "../assets/estilos/alistyle";
import DateTimePicker from '@react-native-datetimepicker/datetimepicker';
import { getFirestore, collection, addDoc } from "firebase/firestore";
import { initializeApp } from "firebase/app";
import { firebaseConfig } from "../firebaseconfig";

const firebaseApp = initializeApp(firebaseConfig)
const db = getFirestore(firebaseApp)

export default function AlquilarCarro({ route }) {
    const {control, handleSubmit, formState} = useForm()
    const { errors} = formState
    const [message, setMessage] = useState('')
    const [messageColor, setMessageColor] = useState(true)
    const [selectedDate, setSelectedDate] = useState(new Date())
    const [show, setShow] = useState(false)

    const onSubmit = async (data) => {
        const isValid = !errors.numeroAlquiler && !errors.nombreUsuario && !errors.numeroPlaca && !errors.fechaAlquiler
        if (isValid) {
            //Crear registro de alquiler
            const alquilerCollection = collection(db, 'alquileres')
            const nuevoAlquiler = {
                numeroAlquiler: data.nuevoAlquiler,
                nombreUsuario: data.nombreUsuario,
                numeroPlaca: data.numeroPlaca,
                fechaAlquiler: selectedDate,
            }
    const onChange = (Event, selectedDate) => {
        setShow(Platform.OS === 'ios')
        setDia(selectedDate || date)
    }        
            try {
                setSelectedDate(new Date())
                setMessageColor(true)
                setMessage('Alquiler exitoso')
            }catch (error) {
                setMessageColor(false)
                setMessage('Error al realizar el alquiler')
            }
        }else {
            setMessageColor(false)
            setMessage('Verifique los datos del formulario')
        }
    }
    //Funcion para mostrar fecha
    const mostrarFecha = () => {
        setShow(true)
    }
    const ocultarFecha = () => {
        setShow(false)
    }
    const confirmarFecha = (Event, selectedDate) => {
        if (selectedDate){
            setSelectedDate(selectedDate)
        }
        ocultarFecha()
    }
    return (
        <ScrollView contentContainerStyle={styles.container}>
            <Text style={styles.title}>Alquiler de Carro</Text>
            <Controller
            control={control}
            render={({ field: {onChange, onBlur, value}}) => (
                <TextInput
                    label= "Numero de alquiler"
                    onBlur={onBlur}
                    onChangeText={onChange}
                    value={value}
                />
            )}
            name="numeroAlquiler"
            rules={{
                required: true
            }}
            />
             <Controller
            control={control}
            render={({ field: {onChange, onBlur, value}}) => (
                <TextInput
                    label= "Nombre de usuario"
                    onBlur={onBlur}
                    onChangeText={onChange}
                    value={value}
                />
            )}
            name="nombreUsuario"
            rules={{
                required: true
            }}
            />
             <Controller
            control={control}
            render={({ field: {onChange, onBlur, value}}) => (
                <TextInput
                    label= "Numero de placa"
                    onBlur={onBlur}
                    onChangeText={onChange}
                    value={value}
                />
            )}
            name="numeroPlaca"
            rules={{
                required: true
            }}
            />
            <View>
                <Button  onPress={() => setShow(true)} title="Seleccionar Fecha"/>
                <Text>{selectedDate.toDateString()}</Text>
            </View>   
            {show && (
                <DateTimePicker
                    value={selectedDate}
                    mode="date"
                    display="default"
                    onChange={confirmarFecha}
                />
                )}
                <Button title="Alquilar" onPress={handleSubmit(onSubmit)}/>
                {message && (
                    <Text style={{ color: messageColor ? 'green' : 'red'}}>{message}</Text>
            )}
        </ScrollView>
    )
}
